angular.module('myApp.search', ['ngRoute'])

    .config(['$routeProvider', function ($routeProvider) {
        $routeProvider
            .when('/search', {
                templateUrl: 'search/index.html',
                controller: 'SearchController'
            })
    }])

    .controller('SearchController', function () {
        console.log("In Search Controller...");
    });

.factory('SearchService', function ($http) {
        var service = {
            query: function (term) {
                return $http.get('/search/' + term);
            }
        };
        return service;
    });

.controller('SearchController', function ($scope, SearchService) {
        $scope.search = function () {
            console.log("Search term is: " + $scope.term);
            SearchService.query($scope.term).then(function (response) {
                $scope.searchResults = response.data;
            });
        };
    })

angular.module('myApp')
    .service('ServerDataModel', function ServerDataModel() {
        this.data = [
            {
                id: 1,
                name: "Peyton Manning",
                phone: "(303) 567-8910",
                address: {
                    street: "1234 Main Street",
                    city: "Greenwood Village",
                    state: "CO",
                    zip: "80111"
                }
            },
            {
                id: 2,
                name: "Demaryius Thomas",
                phone: "(720) 213-9876",
                address: {
                    street: "5555 Marion Street",
                    city: "Denver",
                    state: "CO",
                    zip: "80202"
                }
            },
            {
                id: 3,
                name: "Von Miller",
                phone: "(917) 323-2333",
                address: {
                    street: "14 Mountain Way",
                    city: "Vail",
                    state: "CO",
                    zip: "81657"
                }
            }
        ];

        this.getData = function () {
            return this.data;
        };

        this.search = function (term) {
            if (term == "" || term == "*") {
                return this.getData();
            }
            // find the name that matches the term
            var list = $.grep(this.getData(), function (element, index) {
                term = term.toLowerCase();
                return (element.name.toLowerCase().match(term));
            });

            if (list.length === 0) {
                return [];
            } else {
                return list;
            }
        };
    })
    .run(function ($httpBackend, ServerDataModel) {

        $httpBackend.whenGET(/\/search\/\w+/).respond(function (method, url, data) {
            // parse the matching URL to pull out the term (/search/:term)
            var term = url.split('/')[2];

            var results = ServerDataModel.search(term);

            return [200, results, {Location: '/search/' + term}];
        });

        $httpBackend.whenGET(/search\/index.html/).passThrough();
        $httpBackend.whenGET(/view/).passThrough();
    });

$scope.edit = function (person) {
            $location.path("/edit/" + person.id);
        }

fetch: function (id) {
                return $http.get('/edit/' + id);
            },
            save: function(data) {
                return $http.post('/edit/' + data.id, data);
            }

$httpBackend.whenGET(/\/search/).respond(function (method, url, data) {
            var results = ServerDataModel.search("");

            return [200, results];
        });

        $httpBackend.whenGET(/\/edit\/\d+/).respond(function (method, url, data) {
            // parse the matching URL to pull out the id (/edit/:id)
            var id = url.split('/')[2];

            var results = ServerDataModel.find(id);

            return [200, results, {Location: '/edit/' + id}];
        });

        $httpBackend.whenPOST(/\/edit\/\d+/).respond(function(method, url, data) {
            var params = angular.fromJson(data);

            // parse the matching URL to pull out the id (/edit/:id)
            var id = url.split('/')[2];

            var person = ServerDataModel.update(id, params);

            return [201, person, { Location: '/edit/' + id }];
        });

        $httpBackend.whenGET(/search\/edit.html/).passThrough();

this.find = function (id) {
            // find the game that matches that id
            var list = $.grep(this.getData(), function (element, index) {
                return (element.id == id);
            });
            if (list.length === 0) {
                return {};
            }
            // even if list contains multiple items, just return first one
            return list[0];
        };

        this.update = function (id, dataItem) {
            // find the game that matches that id
            var people = this.getData();
            var match = null;
            for (var i = 0; i < people.length; i++) {
                if (people[i].id == id) {
                    match = people[i];
                    break;
                }
            }
            if (!angular.isObject(match)) {
                return {};
            }
            angular.extend(match, dataItem);
            return match;
        };

if ($routeParams.term) {
            SearchService.query($routeParams.term).then(function (response) {
                $scope.term = $routeParams.term;
                $scope.searchResults = response.data;
            });
        }

.when('/search/:term', {
                templateUrl: 'search/index.html',
                controller: 'SearchController'
            })

describe('save person', function() {
        var name = element(by.model('person.name'));
        var save = element(by.id('save'));

        beforeEach(function() {
            browser.get('index.html#/edit/1');
        });

        it('should allow updating a name', function() {
            name.sendKeys(" Updated");
            save.click().then(function() {
                // verify url set back to search results
                browser.driver.wait(function() {
                    return browser.driver.getCurrentUrl().then(function(url) {
                        expect(url).toContain('/search/Peyton%20Manning%20Updated');
                        return url;
                    });
                });
                // verify one element matched this change
                expect(element.all(by.repeater('person in searchResults')).count()).toEqual(1);
            });
        });
    });

describe('search', function() {
        var searchTerm = element(by.model('term'));
        var searchButton = element(by.id('search'));

        beforeEach(function() {
            browser.get('index.html#/search');
        });

        it('should allow searching at /search', function() {
            searchTerm.sendKeys("M");
            searchButton.click().then(function() {
                expect(element.all(by.repeater('person in searchResults')).count()).toEqual(3);
            });
        });
    });

describe('search by term automatically', function() {
    var scope, rootScope, controller, location, deferred;

    beforeEach(inject(function($rootScope, $controller, $q) {
        rootScope = $rootScope;
        scope = $rootScope.$new();

        // in this case, expectations need to be setup before controller is initialized
        var routeParams = {"term": "peyton"};
        deferred = $q.defer();
        spyOn(mockSearchService, 'query').andReturn(deferred.promise);
        deferred.resolve({data: {name: "Peyton Manning"}});

        controller = $controller('SearchController', {$scope: scope, $routeParams: routeParams, SearchService: mockSearchService });
    }));

    it('should search automatically when a term is on the URL', function() {
        rootScope.$apply();
        expect(scope.searchResults).toEqual({name: "Peyton Manning"});
    });
});

describe('search by term', function() {
    var scope, rootScope, controller, deferred;

    // setup the controller with dependencies.
    beforeEach(inject(function($rootScope, $controller, $q) {
        rootScope = $rootScope;
        scope = $rootScope.$new();
        controller = $controller('SearchController', {$scope: scope, SearchService: mockSearchService });
        deferred = $q.defer();
    }));

    it('should search when a term is set and search() is called', function() {
        spyOn(mockSearchService, 'query').andReturn(deferred.promise);
        scope.term = 'M';
        scope.search();
        deferred.resolve({data: {name: "Peyton Manning"}});
        rootScope.$apply();
        expect(scope.searchResults).toEqual({name: "Peyton Manning"});
    });
});

