import java.util.Collections;
import java.util.List;
import java.util.regex.Matcher;
import java.util.regex.Pattern;

import static org.junit.Assert.assertEquals;
import static org.junit.Assert.assertTrue;

/**
 * Basic integration tests for demo application.
 *
 * @author Dave Syer
 */
@org.junit.runner.RunWith(valueorg.springframework.test.context.junit4.SpringJUnit4ClassRunner.class)
@org.springframework.boot.test.SpringApplicationConfiguration(classes = {security.Application.class})
@org.springframework.test.context.web.WebAppConfiguration
@org.springframework.boot.test.IntegrationTest(value{"server.port:0", "server.ssl.enabled:false"})
@org.springframework.test.annotation.DirtiesContext
public class WebSecurityTests {

    @org.springframework.beans.factory.annotation.Value(value"${local.server.port}")
    private int port;

    @org.junit.Test
    public void testHome() throws java.lang.Exception {
        org.springframework.http.HttpHeaders headers = new org.springframework.http.HttpHeaders();
        headers.setAccept(java.util.Collections.singletonList(org.springframework.http.MediaType.TEXT_HTML));
        org.springframework.http.ResponseEntity<java.lang.String> entity = new org.springframework.boot.test.TestRestTemplate().exchange(
                "http://localhost:" + this.port, org.springframework.http.HttpMethod.GET, new org.springframework.http.HttpEntity<java.lang.Void>(
                        headers), java.lang.String.class);
        assertEquals(org.springframework.http.HttpStatus.OK, entity.getStatusCode());
        assertTrue("Wrong body (title doesn't match):
" + entity.getBody(), entity
                .getBody().contains("<title>Spring"));
    }

    @org.junit.Test
    public void testLogin() throws java.lang.Exception {
        org.springframework.http.HttpHeaders headers = new org.springframework.http.HttpHeaders();
        headers.setAccept(java.util.Collections.singletonList(org.springframework.http.MediaType.TEXT_HTML));
        org.springframework.util.MultiValueMap<java.lang.String, java.lang.String> form = new org.springframework.util.LinkedMultiValueMap<>();
        form.set("username", "user");
        form.set("password", "password");
        form.set("remember-me", "true");
        getCsrf(form, headers);
        org.springframework.http.ResponseEntity<java.lang.String> entity = new org.springframework.boot.test.TestRestTemplate().exchange(
                "http://localhost:" + this.port + "/login", org.springframework.http.HttpMethod.POST,
                new org.springframework.http.HttpEntity<>(form, headers),
                java.lang.String.class);
        assertEquals(org.springframework.http.HttpStatus.FOUND, entity.getStatusCode());
        java.util.List<java.lang.String> cookies = entity.getHeaders().get("Set-Cookie");
        assertTrue(cookies.toString().contains("remember-me"));
        assertEquals("http://localhost:" + this.port + "/", entity.getHeaders()
                .getLocation().toString());
    }

    @org.junit.Test
    public void testDenied() throws java.lang.Exception {
        org.springframework.http.HttpHeaders headers = new org.springframework.http.HttpHeaders();
        headers.setAccept(java.util.Collections.singletonList(org.springframework.http.MediaType.TEXT_HTML));
        org.springframework.util.MultiValueMap<java.lang.String, java.lang.String> form = new org.springframework.util.LinkedMultiValueMap<>();
        form.set("username", "admin");
        form.set("password", "admin");
        getCsrf(form, headers);
        org.springframework.http.ResponseEntity<java.lang.String> entity = new org.springframework.boot.test.TestRestTemplate().exchange(
                "http://localhost:" + this.port + "/login", org.springframework.http.HttpMethod.POST,
                new org.springframework.http.HttpEntity<>(form, headers),
                java.lang.String.class);
        assertEquals(org.springframework.http.HttpStatus.FOUND, entity.getStatusCode());
        java.lang.String cookie = entity.getHeaders().getFirst("Set-Cookie");
        headers.set("Cookie", cookie);
        org.springframework.http.ResponseEntity<java.lang.String> page = new org.springframework.boot.test.TestRestTemplate().exchange(entity.getHeaders()
                        .getLocation(), org.springframework.http.HttpMethod.GET, new org.springframework.http.HttpEntity<java.lang.Void>(headers),
                java.lang.String.class);
        assertEquals(org.springframework.http.HttpStatus.OK, page.getStatusCode());
        cookie = entity.getHeaders().getFirst("Set-Cookie");
        assertTrue(cookie.contains("remember-me"));
        assertTrue("Wrong body (message doesn't match):
" + entity.getBody(), page
                .getBody().contains("Invalid username and password"));
    }

    @org.junit.Test
    public void testProtected() throws java.lang.Exception {
        org.springframework.http.ResponseEntity<java.lang.String> entity = new org.springframework.boot.test.TestRestTemplate().getForEntity(
                "http://localhost:" + this.port + "/api/health", java.lang.String.class);
        assertTrue("Wrong body (message doesn't match):

" + entity.getBody(), entity
                .getBody().contains("Sign In"));
    }

    @org.junit.Test
    public void testAuthorizedAccess() throws java.lang.Exception {
        org.springframework.http.ResponseEntity<java.lang.String> entity = new org.springframework.boot.test.TestRestTemplate("user", "user")
                .getForEntity("http://localhost:" + this.port + "/api/health", java.lang.String.class);
        assertEquals(org.springframework.http.HttpStatus.OK, entity.getStatusCode());
    }

    @org.junit.Test
    public void testUnauthorizedAccess() throws java.lang.Exception {
        org.springframework.http.ResponseEntity<java.lang.String> entity = new org.springframework.boot.test.TestRestTemplate("admin", "admin")
                .getForEntity("http://localhost:" + this.port + "/api/health", java.lang.String.class);
        assertEquals(org.springframework.http.HttpStatus.OK, entity.getStatusCode());
        assertTrue("Wrong body (message doesn't match):

" + entity.getBody(), entity
                .getBody().contains("Sign In"));
    }

    private void getCsrf(org.springframework.util.MultiValueMap<java.lang.String, java.lang.String> form, org.springframework.http.HttpHeaders headers) {
        org.springframework.http.ResponseEntity<java.lang.String> page = new org.springframework.boot.test.TestRestTemplate().getForEntity(
                "http://localhost:" + this.port + "/login", java.lang.String.class);
        java.lang.String cookie = page.getHeaders().getFirst("Set-Cookie");
        headers.set("Cookie", cookie);
        java.lang.String body = page.getBody();
        java.util.regex.Matcher matcher = java.util.regex.Pattern.compile("(?s).*name="_csrf".*?value="([^"]+).*")
                .matcher(body);
        matcher.find();
        form.set("_csrf", matcher.group(1));
    }

}

package security;

public class LoginStatus {

    private final boolean loggedIn;
    private final java.lang.String username;

    public LoginStatus(boolean loggedIn, java.lang.String username) {
        this.loggedIn = loggedIn;
        this.username = username;
    }

    public boolean isLoggedIn() {
        return loggedIn;
    }

    public java.lang.String getUsername() {
        return username;
    }
}

@org.springframework.beans.factory.annotation.Autowired
    public void configureGlobal(org.springframework.security.config.annotation.authentication.builders.AuthenticationManagerBuilder auth) throws java.lang.Exception {
        auth.jdbcAuthentication().dataSource(this.dataSource);
    }

package security;

import org.apache.shiro.realm.text.PropertiesRealm;
import org.apache.shiro.spring.LifecycleBeanPostProcessor;
import org.apache.shiro.spring.web.ShiroFilterFactoryBean;
import org.apache.shiro.web.filter.authc.AnonymousFilter;
import org.apache.shiro.web.filter.authc.FormAuthenticationFilter;
import org.apache.shiro.web.filter.authc.LogoutFilter;
import org.apache.shiro.web.filter.authc.UserFilter;
import org.apache.shiro.web.filter.authz.RolesAuthorizationFilter;
import org.apache.shiro.web.mgt.DefaultWebSecurityManager;
import org.apache.shiro.web.servlet.AbstractShiroFilter;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.context.annotation.DependsOn;

import javax.servlet.Filter;
import java.util.HashMap;
import java.util.Map;

@Configuration
public class WebSecurityConfig {

    @Bean(name = "shiroFilter")
    public AbstractShiroFilter shiroFilter() throws Exception {
        ShiroFilterFactoryBean shiroFilter = new ShiroFilterFactoryBean();
        Map<String, String> filterChainDefinitionMapping = new HashMap<>();
        filterChainDefinitionMapping.put("/api/health", "authcBasic,roles[guest]");
        shiroFilter.setFilterChainDefinitionMap(filterChainDefinitionMapping);
        shiroFilter.setSecurityManager(securityManager());
        Map<String, Filter> filters = new HashMap<>();
        filters.put("anon", new AnonymousFilter());
        filters.put("authc", new FormAuthenticationFilter());
        LogoutFilter logoutFilter = new LogoutFilter();
        logoutFilter.setRedirectUrl("/login?logout");
        filters.put("roles", new RolesAuthorizationFilter());
        filters.put("user", new UserFilter());
        shiroFilter.setFilters(filters);
        return (AbstractShiroFilter) shiroFilter.getObject();
    }

    @Bean(name = "securityManager")
    public DefaultWebSecurityManager securityManager() {
        DefaultWebSecurityManager securityManager = new DefaultWebSecurityManager();
        securityManager.setRealm(realm());
        return securityManager;
    }

    @Bean(name = "realm")
    @DependsOn("lifecycleBeanPostProcessor")
    public PropertiesRealm realm() {
        PropertiesRealm propertiesRealm = new PropertiesRealm();
        propertiesRealm.init();
        return propertiesRealm;
    }

    @Bean
    public static LifecycleBeanPostProcessor lifecycleBeanPostProcessor() {
        return new LifecycleBeanPostProcessor();
    }
}

LogoutFilter logoutFilter = new LogoutFilter();
        logoutFilter.setRedirectUrl("/login?logout");
        filters.put("logout", logoutFilter);

