  username VARCHAR(256),
  password VARCHAR(256),
  enabled  BOOLEAN
);

CREATE TABLE IF NOT EXISTS authorities (
  username  VARCHAR(256),
  authority VARCHAR(256)
);

package security;

import static org.springframework.test.web.servlet.request.MockMvcRequestBuilders.*;
import static org.springframework.test.web.servlet.result.MockMvcResultHandlers.*;
import static org.springframework.test.web.servlet.result.MockMvcResultMatchers.*;

import org.apache.shiro.web.servlet.AbstractShiroFilter;
import org.junit.Before;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.test.SpringApplicationConfiguration;
import org.springframework.test.context.junit4.SpringJUnit4ClassRunner;
import org.springframework.test.context.web.WebAppConfiguration;
import org.springframework.test.web.servlet.MockMvc;
import org.springframework.test.web.servlet.request.MockHttpServletRequestBuilder;
import org.springframework.test.web.servlet.setup.MockMvcBuilders;
import org.springframework.web.context.WebApplicationContext;

@org.junit.runner.RunWith(SpringJUnit4ClassRunner.class)
@org.springframework.boot.test.SpringApplicationConfiguration(classes = security.Application.class)
@org.springframework.test.context.web.WebAppConfiguration
public class ApplicationTests {
    @org.springframework.beans.factory.annotation.Autowired
    org.springframework.web.context.WebApplicationContext context;
    @org.springframework.beans.factory.annotation.Autowired
    org.apache.shiro.web.servlet.AbstractShiroFilter shiroFilter;

    org.springframework.test.web.servlet.MockMvc mockMvc;

    @org.junit.Before
    public void setup() {
        mockMvc = org.springframework.test.web.servlet.setup.MockMvcBuilders
                .webAppContextSetup(context)
                .addFilters(shiroFilter)
                .alwaysDo(print())
                .build();
    }

    @org.junit.Test
    public void apiNeedsAuthentication() throws java.lang.Exception {
        mockMvc
                .perform(get("/api/health"))
                .andExpect(status().is3xxRedirection());
    }

    @org.junit.Test
    public void loginSuccess() throws java.lang.Exception {
        org.springframework.test.web.servlet.request.MockHttpServletRequestBuilder loginRequest = post("/login")
                .param("username", "admin")
                .param("password", "adminjdbc");
        mockMvc
                .perform(loginRequest)
                .andExpect(status().is3xxRedirection());
    }

    @org.junit.Test
    public void invalidUsernamePassword() throws java.lang.Exception {
        org.springframework.test.web.servlet.request.MockHttpServletRequestBuilder loginRequest = post("/login")
                .param("username", "admin")
                .param("password", "INVALID");
        mockMvc
                .perform(loginRequest)
                .andExpect(status().isOk());
    }
}

public org.springframework.context.support.PropertySourcesPlaceholderConfigurer properties() {
        org.springframework.context.support.PropertySourcesPlaceholderConfigurer configurer = new org.springframework.context.support.PropertySourcesPlaceholderConfigurer();
        configurer.setIgnoreUnresolvablePlaceholders(true);
        configurer.setLocations(new org.springframework.core.io.ClassPathResource("jdbc.properties"),
                                new org.springframework.core.io.ClassPathResource("mail.properties"),
                                new org.springframework.core.io.ClassPathResource("hibernate.properties"));
        return configurer;
    }

@org.springframework.context.annotation.Bean
    public org.apache.commons.dbcp.BasicDataSource dataSource() {
        org.apache.commons.dbcp.BasicDataSource dataSource = new org.apache.commons.dbcp.BasicDataSource();
        dataSource.setDriverClassName(driverClassName);
        dataSource.setUrl(url);
        dataSource.setUsername(username);
        dataSource.setPassword(password);
        dataSource.setMaxActive(100);
        dataSource.setMaxWait(1000);
        dataSource.setPoolPreparedStatements(true);
        dataSource.setDefaultAutoCommit(true);
        dataSource.setValidationQuery(validationQuery);
        dataSource.setTestOnBorrow(true);
        return dataSource;
    }

@org.springframework.beans.factory.annotation.Value(value"${jdbc.driverClassName}")
    private java.lang.String driverClassName;

    @org.springframework.beans.factory.annotation.Value(value"${jdbc.url}")
    private java.lang.String url;

    @org.springframework.beans.factory.annotation.Value(value"${jdbc.username}")
    private java.lang.String username;

    @org.springframework.beans.factory.annotation.Value(value"${jdbc.password}")
    private java.lang.String password;

    @org.springframework.beans.factory.annotation.Value(value"${jdbc.validationQuery}")
    private java.lang.String validationQuery;

@org.springframework.context.annotation.Bean
    public org.springframework.orm.jpa.persistenceunit.DefaultPersistenceUnitManager persistenceUnitManager() {
        org.springframework.orm.jpa.persistenceunit.DefaultPersistenceUnitManager unitManager = new org.springframework.orm.jpa.persistenceunit.DefaultPersistenceUnitManager();
        unitManager.setDefaultDataSource(dataSource);
        return unitManager;
    }

@org.springframework.beans.factory.annotation.Value(value"${hibernate.dialect}")
    private java.lang.String hibernateDialect;

    @org.springframework.beans.factory.annotation.Value(value"${app.search.index.basedir}")
    private java.lang.String searchBaseDir;

    @org.springframework.context.annotation.Bean(name = "entityManagerFactory")
    @org.springframework.beans.factory.annotation.Autowired
    public org.springframework.orm.jpa.LocalContainerEntityManagerFactoryBean entityManagerFactoryBean(org.springframework.orm.jpa.persistenceunit.PersistenceUnitManager unitManager) {
        org.springframework.orm.jpa.LocalContainerEntityManagerFactoryBean entityManagerFactoryBean = new org.springframework.orm.jpa.LocalContainerEntityManagerFactoryBean();
        entityManagerFactoryBean.setPersistenceUnitManager(unitManager);
        java.util.Properties properties = new java.util.Properties();
        properties.setProperty("hibernate.dialect", hibernateDialect);
        properties.setProperty("hibernate.query.substitutions", "true 'Y', false 'N'");
        properties.setProperty("hibernate.cache.use_second_level_cache", "true");
        properties.setProperty("hibernate.cache.provider_class", "org.hibernate.cache.EhCacheProvider");
        properties.setProperty("hibernate.search.default.indexBase", searchBaseDir);
        entityManagerFactoryBean.setJpaProperties(properties);
        return entityManagerFactoryBean;
    }

@org.springframework.context.annotation.Bean
    @org.springframework.beans.factory.annotation.Autowired
    public org.springframework.orm.jpa.JpaTransactionManager transactionManager(org.springframework.orm.jpa.LocalContainerEntityManagerFactoryBean entityManagerFactory) {
        org.springframework.orm.jpa.JpaTransactionManager jpaTransactionManager = new org.springframework.orm.jpa.JpaTransactionManager();
        jpaTransactionManager.setEntityManagerFactory(entityManagerFactory.getObject());
        return jpaTransactionManager;
    }

@org.springframework.context.annotation.Bean
    public org.springframework.dao.annotation.PersistenceExceptionTranslationPostProcessor exceptionTranslation(){
        return new org.springframework.dao.annotation.PersistenceExceptionTranslationPostProcessor();
    }

@Configuration
@org.springframework.transaction.annotation.EnableTransactionManagement
@org.springframework.context.annotation.EnableAspectJAutoProxy
@org.springframework.context.annotation.ComponentScan(value"com.raibledesigns.service")
public class ServiceConfig {

    @org.springframework.context.annotation.Bean
    public com.raibledesigns.service.MailEngine mailEngine() {
        com.raibledesigns.service.MailEngine mailEngine = new com.raibledesigns.service.MailEngine();
        mailEngine.setVelocityEngine(velocityEngine().getObject());
        mailEngine.setFrom("${mail.default.from}");
        return mailEngine;
    }

    @org.springframework.context.annotation.Bean
    public org.springframework.mail.javamail.JavaMailSenderImpl mailSender(@org.springframework.beans.factory.annotation.Value(value"${mail.host}") java.lang.String mailHost) {
        org.springframework.mail.javamail.JavaMailSenderImpl mailSender = new org.springframework.mail.javamail.JavaMailSenderImpl();
        mailSender.setHost(mailHost);
        mailSender.setDefaultEncoding("UTF-8");
        return mailSender;
    }

    @org.springframework.context.annotation.Bean
    public org.springframework.ui.velocity.VelocityEngineFactoryBean velocityEngine() {
        org.springframework.ui.velocity.VelocityEngineFactoryBean velocityEngineFactoryBean = new org.springframework.ui.velocity.VelocityEngineFactoryBean();
        java.util.Properties properties = new java.util.Properties();
        properties.setProperty("resource.loader", "class");
        properties.setProperty("class.resource.loader.class", "org.apache.velocity.runtime.resource.loader.ClasspathResourceLoader");
        properties.setProperty("velocimacro.library", "");
        velocityEngineFactoryBean.setVelocityProperties(properties);
        return velocityEngineFactoryBean;
    }

    @org.springframework.context.annotation.Bean
    @org.springframework.context.annotation.Scope(value"prototype")
    public org.springframework.mail.SimpleMailMessage mailMessage() {
        org.springframework.mail.SimpleMailMessage mailMessage = new org.springframework.mail.SimpleMailMessage();
        mailMessage.setFrom("${mail.default.from}");
        return mailMessage;
    }

    @org.springframework.context.annotation.Bean
    public org.springframework.security.crypto.password.PasswordEncoder passwordEncoder() {
        return new org.springframework.security.crypto.bcrypt.BCryptPasswordEncoder();
    }

    @org.springframework.context.annotation.Bean(name = "passwordTokenEncoder")
    public org.springframework.security.crypto.password.PasswordEncoder passwordTokenEncoder() {
        return new org.springframework.security.crypto.password.StandardPasswordEncoder();
    }
}

@org.springframework.context.annotation.Configuration
@org.springframework.security.config.annotation.web.configuration.EnableWebSecurity
public class SecurityConfig extends org.springframework.security.config.annotation.web.configuration.WebSecurityConfigurerAdapter {
    @org.springframework.beans.factory.annotation.Autowired
    private com.raibledesigns.dao.UserDao userDao;

    @org.springframework.beans.factory.annotation.Autowired
    private org.springframework.security.crypto.password.PasswordEncoder passwordEncoder;

    @org.springframework.beans.factory.annotation.Autowired
    public void configureGlobal(org.springframework.security.config.annotation.authentication.builders.AuthenticationManagerBuilder auth) throws java.lang.Exception {
        auth.userDetailsService(userDao)
            .passwordEncoder(passwordEncoder);
    }

    @java.lang.Override
    public void configure(org.springframework.security.config.annotation.web.builders.WebSecurity web) throws java.lang.Exception {
        web.ignoring()
                .antMatchers("/images/**")
                .antMatchers("/styles/**")
                .antMatchers("/scripts/**")
                .antMatchers("/assets/**");
    }

    @java.lang.Override
    protected void configure(org.springframework.security.config.annotation.web.builders.HttpSecurity http) throws java.lang.Exception {
        http
            .csrf()
            .disable()
            .headers()
            .frameOptions()
            .disable()
            .authorizeRequests()
            .antMatchers("/app/admin/**").hasAuthority("ROLE_ADMIN")
            .antMatchers("/app/passwordHint*").permitAll()
            .antMatchers("/app/requestRecoveryToken*").permitAll()
            .antMatchers("/app/updatePassword*").permitAll()
            .antMatchers("/app/signup*").permitAll()
            .antMatchers("/app/**").hasAnyAuthority("ROLE_ADMIN","ROLE_USER")
        .and()
            .formLogin()
                .loginPage("/login")
                .usernameParameter("j_username")
                .passwordParameter("j_password")
                .loginProcessingUrl("/j_security_check")
                .failureUrl("/login?error=true")
        .and()
            .rememberMe()
                .userDetailsService(userDao)
                .key("e37f4b31-0c45-11dd-bd0b-0800200c9a66");
    }
}

public class WebAppInitializer implements org.springframework.web.WebApplicationInitializer {

    @java.lang.Override
    public void onStartup(javax.servlet.ServletContext container) throws javax.servlet.ServletException {
        container.setInitParameter("javax.servlet.jsp.jstl.fmt.localizationContext", "ApplicationResources");
        container.setInitParameter("javax.servlet.jsp.jstl.fmt.fallbackLocale", "en");
        container.setInitParameter("contextClass", "org.springframework.web.context.support.AnnotationConfigWebApplicationContext");
        container.setInitParameter("contextConfigLocation",
                "com.raibledesigns.config.ResourcesConfig,com.raibledesigns.config.JpaConfig," +
                "com.raibledesigns.config.ServiceConfig,com.raibledesigns.config.SecurityConfig");

        javax.servlet.FilterRegistration.Dynamic encodingFilter = container.addFilter("encodingFilter", org.springframework.web.filter.CharacterEncodingFilter.class);
        encodingFilter.setInitParameter("encoding", "UTF-8");
        encodingFilter.setInitParameter("forceEncoding", "true");
        encodingFilter.addMappingForUrlPatterns(null, false, "/*");

        javax.servlet.FilterRegistration.Dynamic exportFilter = container.addFilter("exportFilter", org.displaytag.filter.ResponseOverrideFilter.class);
        exportFilter.addMappingForUrlPatterns(null, false, "/app/*");

        javax.servlet.FilterRegistration.Dynamic localeFilter = container.addFilter("localeFilter", com.raibledesigns.webapp.filter.LocaleFilter.class);
        localeFilter.addMappingForUrlPatterns(null, false, "/*");

        javax.servlet.FilterRegistration.Dynamic rewriteFilter = container.addFilter("rewriteFilter", org.tuckey.web.filters.urlrewrite.UrlRewriteFilter.class);
        rewriteFilter.setInitParameter("logLevel", "commons");
        rewriteFilter.setInitParameter("confReloadCheckInterval", "-1");
        rewriteFilter.addMappingForUrlPatterns(null, false, "/*");

        javax.servlet.FilterRegistration.Dynamic securityFilter = container.addFilter("securityFilter", org.springframework.web.filter.DelegatingFilterProxy.class);
        securityFilter.setInitParameter("targetBeanName", "springSecurityFilterChain");
        securityFilter.addMappingForUrlPatterns(java.util.EnumSet.of(
                javax.servlet.DispatcherType.REQUEST, javax.servlet.DispatcherType.FORWARD, javax.servlet.DispatcherType.INCLUDE), false, "/*");

        javax.servlet.FilterRegistration.Dynamic sitemeshFilter = container.addFilter("sitemeshFilter", com.opensymphony.sitemesh.webapp.SiteMeshFilter.class);
        sitemeshFilter.addMappingForUrlPatterns(java.util.EnumSet.of(
                javax.servlet.DispatcherType.REQUEST, javax.servlet.DispatcherType.FORWARD), false, "/*");

        javax.servlet.FilterRegistration.Dynamic wroFilter = container.addFilter("wroFilter", ro.isdc.wro.http.WroFilter.class);
        wroFilter.addMappingForUrlPatterns(java.util.EnumSet.of(
                javax.servlet.DispatcherType.REQUEST, javax.servlet.DispatcherType.FORWARD), false, "/assets/*");

        javax.servlet.ServletRegistration.Dynamic cxfServlet = container.addServlet("CXFServlet", org.apache.cxf.transport.servlet.CXFServlet.class);
        cxfServlet.addMapping("/services/*");

        container.addListener(org.springframework.web.context.ContextLoaderListener.class);
        container.addListener(com.raibledesigns.webapp.listener.StartupListener.class);
        container.addListener(com.raibledesigns.webapp.listener.UserCounterListener.class);
        container.addListener(com.raibledesigns.webapp.jsp.EscapeXmlELResolverListener.class);
        container.addListener(net.sf.navigator.menu.MenuContextListener.class);

        org.springframework.web.context.support.XmlWebApplicationContext appContext = new org.springframework.web.context.support.XmlWebApplicationContext();
        appContext.setConfigLocation("/WEB-INF/dispatcher-servlet.xml");

        javax.servlet.ServletRegistration.Dynamic dispatcher =
                container.addServlet("dispatcher", new org.springframework.web.servlet.DispatcherServlet(appContext));
        dispatcher.setLoadOnStartup(1);
        dispatcher.addMapping("/app/*");
    }
}

@org.springframework.context.annotation.Configuration
@org.springframework.web.servlet.config.annotation.EnableWebMvc
@org.springframework.context.annotation.ComponentScan(value"com.raibledesigns.webapp")
public class WebConfig extends org.springframework.web.servlet.config.annotation.WebMvcConfigurerAdapter {

    @org.springframework.context.annotation.Bean
    public org.springframework.web.servlet.handler.SimpleMappingExceptionResolver exceptionResolver() {
        org.springframework.web.servlet.handler.SimpleMappingExceptionResolver exceptionResolver = new org.springframework.web.servlet.handler.SimpleMappingExceptionResolver();

        java.util.Properties exceptionMappings = new java.util.Properties();
        exceptionMappings.put("org.springframework.dao.DataAccessException", "dataAccessFailure");
        exceptionResolver.setExceptionMappings(exceptionMappings);

        return exceptionResolver;
    }

    @org.springframework.context.annotation.Bean
    public org.springframework.web.multipart.MultipartResolver multipartResolver() {
        org.springframework.web.multipart.commons.CommonsMultipartResolver multipartResolver = new org.springframework.web.multipart.commons.CommonsMultipartResolver();
        multipartResolver.setMaxUploadSize(2097152);
        return multipartResolver;
    }

    @org.springframework.context.annotation.Bean
    public org.springframework.context.support.ResourceBundleMessageSource messageSource() {
        org.springframework.context.support.ResourceBundleMessageSource messageSource = new org.springframework.context.support.ResourceBundleMessageSource();
        messageSource.setBasename("ApplicationResources");
        messageSource.setUseCodeAsDefaultMessage(true);
        return messageSource;
    }

    @java.lang.Override
    public void addViewControllers(org.springframework.web.servlet.config.annotation.ViewControllerRegistry registry) {
        registry.addViewController("/admin/activeUsers").setViewName("admin/activeUsers");
        registry.addViewController("/home").setViewName("home");
    }

    @org.springframework.context.annotation.Bean
    public org.springframework.web.servlet.ViewResolver viewResolver() {
        org.springframework.web.servlet.view.InternalResourceViewResolver viewResolver = new org.springframework.web.servlet.view.InternalResourceViewResolver();
        viewResolver.setRequestContextAttribute("rc");
        viewResolver.setViewClass(org.springframework.web.servlet.view.JstlView.class);
        viewResolver.setPrefix("/WEB-INF/pages/");
        viewResolver.setSuffix("*.jsp");
        return viewResolver;
    }

@org.springframework.context.annotation.ComponentScan
@org.springframework.boot.autoconfigure.EnableAutoConfiguration
public class Application {
    public static void main(java.lang.String[] args) {
        org.springframework.context.ApplicationContext ctx = org.springframework.boot.SpringApplication.run(com.raibledesigns.Application.class, args);
    }
}

