@org.springframework.cloud.openfeign.FeignClient("beer-catalog-service")
interface BeerClient {

    @org.springframework.web.bind.annotation.GetMapping("/beers")
    org.springframework.hateoas.Resources<com.example.edgeservice.Beer> readBeers();
}

@org.springframework.web.bind.annotation.RestController
class GoodBeerApiAdapterRestController {

    private final com.example.edgeservice.BeerClient beerClient;

    public GoodBeerApiAdapterRestController(com.example.edgeservice.BeerClient beerClient) {
        this.beerClient = beerClient;
    }

    @org.springframework.web.bind.annotation.GetMapping("/good-beers")
    public java.util.Collection<com.example.edgeservice.Beer> goodBeers() {
        return beerClient.readBeers()
                .getContent()
                .stream()
                .filter(this::isGreat)
                .collect(java.util.stream.Collectors.toList());
    }

    private boolean isGreat(com.example.edgeservice.Beer beer) {
        return !beer.getName().equals("Budweiser") &&
                !beer.getName().equals("Coors Light") &&
                !beer.getName().equals("PBR");
    }
}

public java.util.Collection<com.example.edgeservice.Beer> fallback() {
        return new java.util.ArrayList<>();
    }

    @com.netflix.hystrix.contrib.javanica.annotation.HystrixCommand(fallbackMethod = "fallback")

import org.springframework.context.annotation.Configuration;
import org.springframework.security.config.annotation.web.builders.HttpSecurity;
import org.springframework.security.oauth2.config.annotation.web.configuration.EnableResourceServer;
import org.springframework.security.oauth2.config.annotation.web.configuration.ResourceServerConfigurerAdapter;
import org.springframework.security.web.util.matcher.RequestHeaderRequestMatcher;

@org.springframework.context.annotation.Configuration
@EnableResourceServer
public class ResourceServerConfig extends ResourceServerConfigurerAdapter {

    @java.lang.Override
    public void configure(org.springframework.security.config.annotation.web.builders.HttpSecurity http) throws java.lang.Exception {
        http
            .requestMatcher(new org.springframework.security.web.util.matcher.RequestHeaderRequestMatcher("Authorization"))
            .authorizeRequests()
            .antMatchers("/**").authenticated();
    }
}
